import React from "react";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { View, Text } from "react-native";
import { MaterialIcons } from '@expo/vector-icons';

// --- 1. IMPORT StackNavigator and TempleDetailScreen ---
import { createStackNavigator } from "@react-navigation/stack";
import HomeScreen from "../screens/HomeScreen";
import PlanTripScreen from "../screens/PlanTripScreen";
import DonationScreen from "../screens/DonationScreen";
import ProfileScreen from "../screens/ProfileScreen";
import TempleDetailScreen from "../screens/TempleDetailScreen"; // <-- IMPORT NEW

const Tab = createBottomTabNavigator();
const Stack = createStackNavigator(); // <-- 2. CREATE STACK

const TabIcon = ({ name, color, size }) => (
  <MaterialIcons name={name} size={size} color={color} />
);

// --- 3. CREATE A STACK FOR YOUR HOME TAB ---
// This stack will contain HomeScreen and TempleDetailScreen
// This allows you to navigate *within* the Home tab
function HomeStack() {
  return (
    // Use the same header style as your tabs
    <Stack.Navigator
      screenOptions={{
        headerStyle: { backgroundColor: "#FF6B35" },
        headerTintColor: "#fff",
        headerTitleStyle: { fontWeight: "bold" },
      }}
    >
      <Stack.Screen
        name="HomeStack" // Internal name for this stack's home
        component={HomeScreen}
        options={{ title: "BhaktiBhraman" }} // This is the title you had before
      />
      <Stack.Screen
        name="TempleDetail" // This is the screen you navigate to from HomeScreen
        component={TempleDetailScreen}
        options={{ title: "Temple Details" }}
      />
    </Stack.Navigator>
  );
}
// ---------------------------------------------

export default function TabNavigator() {
  return (
    <Tab.Navigator
      screenOptions={{
        tabBarActiveTintColor: "#FF6B35",
  
        tabBarInactiveTintColor: "#666",
        tabBarStyle: {
          backgroundColor: "#fff",
          borderTopWidth: 1,
          borderTopColor: "#e0e0e0",
          height: 60,
          paddingBottom: 8,
          paddingTop: 8,
        },
        tabBarLabelStyle: {
          fontSize: 12,
          fontWeight: "500",
        },
        // --- 4. HIDE THE TAB'S HEADER ---
        // The StackNavigator (HomeStack) will provide its own header now.
        headerShown: false,
      }}
    >
      <Tab.Screen
        name="Home"
        // --- 5. USE THE HomeStack COMPONENT ---
        component={HomeStack} 
        options={{
          tabBarIcon: ({ color, size }) => (
            <TabIcon name="home" color={color} size={size} />
          ),
          // Title is now set inside HomeStack, so we remove it from here
          // title: "BhaktiBhraman", // <-- REMOVED
        }}
      />
      <Tab.Screen
        name="Plan"
        component={PlanTripScreen}
        options={{
          headerShown: true, // <-- Show the header for non-stack screens
          headerStyle: { backgroundColor: "#FF6B35" },
          headerTintColor: "#fff",
          headerTitleStyle: { fontWeight: "bold" },
          tabBarIcon: ({ color, size }) => (
            <TabIcon name="flight" color={color} size={size} />
          ),
          title: "Plan Your Trip",
        }}
      />
      {/* --- 6. REMOVED THE BLANK LINE THAT CAUSED THE CRASH --- */}
      <Tab.Screen
        name="Donate"
        component={DonationScreen}
        options={{
          headerShown: true, // <-- Show the header for non-stack screens
          headerStyle: { backgroundColor: "#FF6B35" },
          headerTintColor: "#fff",
          headerTitleStyle: { fontWeight: "bold" },
          tabBarIcon: ({ color, size }) => (
            <View style={{ position: "relative" }}>
              <TabIcon name="volunteer-activism" color={color} size={size} />
              {color === "#FF6B35" && (
                <View
                  style={{
                    position: "absolute",
                    top: -2,
                    right: -2,
                    backgroundColor: "#FF6B35",
                    borderRadius: 8,
                    width: 16,
                    height: 16,
                    justifyContent: "center",
                    alignItems: "center",
                  }}
                >
                  <Text
                    style={{ color: "#fff", fontSize: 10, fontWeight: "bold" }}
                  >
                    6
        _         </Text>
                </View>
              )}
            </View>
          ),
         title: "Temple Donation",
        }}
      />
      <Tab.Screen
        name="Profile"
        component={ProfileScreen}
        options={{
          headerShown: true, // <-- Show the header for non-stack screens
          headerStyle: { backgroundColor: "#FF6B35" },
          headerTintColor: "#fff",
          headerTitleStyle: { fontWeight: "bold" },
          tabBarIcon: ({ color, size }) => (
            <TabIcon name="person" color={color} size={size} />
          ),
          title: "Profile",
        }}
      />
    </Tab.Navigator>
  );
}
