import React from "react";
import { View, Text } from "react-native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createStackNavigator } from "@react-navigation/stack"; // <-- 1. IMPORT STACK NAVIGATOR
import { MaterialIcons } from '@expo/vector-icons';

// --- 2. IMPORT YOUR SCREENS ---
import HomeScreen from "../screens/HomeScreen";
import PlanTripScreen from "../screens/PlanTripScreen";
import DonationScreen from "../screens/DonationScreen";
import ProfileScreen from "../screens/ProfileScreen";
import TempleDetailScreen from "../screens/TempleDetailScreen"; // <-- 3. IMPORT DETAIL SCREEN

const Tab = createBottomTabNavigator();
const Stack = createStackNavigator(); // <-- 4. INITIALIZE STACK

// --- 5. DEFINE HEADER OPTIONS (to reuse) ---
const stackScreenOptions = {
  headerStyle: {
    backgroundColor: "#FF6B35",
  },
  headerTintColor: "#fff",
  headerTitleStyle: {
    fontWeight: "bold",
  },
};

// --- 6. CREATE THE STACK FOR THE 'HOME' TAB ---
// This stack will contain HomeScreen and TempleDetailScreen
function HomeStack() {
  return (
    <Stack.Navigator screenOptions={stackScreenOptions}>
      <Stack.Screen 
        name="HomeStack" // This name is internal to the stack
        component={HomeScreen} 
        options={{ title: "BhaktiBhraman" }} // Header title for Home
      />
      <Stack.Screen 
        name="TempleDetail" // This is the name you navigate to
        component={TempleDetailScreen}
        options={{ title: "Temple Details" }} // Header title for Detail
      />
    </Stack.Navigator>
  );
}

// --- 7. (Optional but recommended) Create stacks for other tabs ---
// This keeps your headers consistent.
function PlanStack() {
  return (
    <Stack.Navigator screenOptions={stackScreenOptions}>
      <Stack.Screen name="PlanTrip" component={PlanTripScreen} options={{ title: "Plan Your Trip" }} />
    </Stack.Navigator>
  );
}

function DonationStack() {
  return (
    <Stack.Navigator screenOptions={stackScreenOptions}>
      <Stack.Screen name="DonateStack" component={DonationScreen} options={{ title: "Temple Donation" }} />
    </Stack.Navigator>
  );
}

function ProfileStack() {
  return (
    <Stack.Navigator screenOptions={stackScreenOptions}>
      <Stack.Screen name="ProfileStack" component={ProfileScreen} options={{ title: "Profile" }} />
    </Stack.Navigator>
  );
}


const TabIcon = ({ name, color, size }) => (
  <MaterialIcons name={name} size={size} color={color} />
);

// --- 8. YOUR MAIN TAB NAVIGATOR ---
export default function TabNavigator() {
  return (
    <Tab.Navigator
      screenOptions={{
        // --- 9. HIDE THE TAB NAVIGATOR'S HEADER ---
        // Each stack will now provide its own header
        headerShown: false, 
        tabBarActiveTintColor: "#FF6B35",
        tabBarInactiveTintColor: "#666",
        tabBarStyle: {
          backgroundColor: "#fff",
          borderTopWidth: 1,
          borderTopColor: "#e0e0e0",
          height: 60,
          paddingBottom: 8,
          paddingTop: 8,
        },
        tabBarLabelStyle: {
          fontSize: 12,
          fontWeight: "500",
        },
      }}
    >
      <Tab.Screen
        name="Home"
        component={HomeStack} // <-- 10. USE THE HOME-STACK COMPONENT
        options={{
          tabBarIcon: ({ color, size }) => (
            <TabIcon name="home" color={color} size={size} />
          ),
          // No title option needed here, stack handles it
        }}
      />
      <Tab.Screen
        name="Plan"
        component={PlanStack} // <-- USE THE PLAN-STACK
        options={{
          tabBarIcon: ({ color, size }) => (
            <TabIcon name="flight" color={color} size={size} />
          ),
        }}
      />
     
      <Tab.Screen
        name="Donate"
        component={DonationStack} // <-- USE THE DONATE-STACK
        options={{
          tabBarIcon: ({ color, size }) => (
            // ... (your cool icon code is unchanged)
            <View style={{ position: "relative" }}>
              <TabIcon name="volunteer-activism" color={color} size={size} />
              {color === "#FF6B35" && (
                <View
                  style={{
                    position: "absolute",
                    top: -2,
                    right: -2,
                    backgroundColor: "#FF6B35",
                    borderRadius: 8,
                    width: 16,
              _ }}
                >
                  <Text
                    style={{ color: "#fff", fontSize: 10, fontWeight: "bold" }}
                  >
                    6
                  </Text>
                </View>
              )}
            </View>
          ),
        }}
      />
      <Tab.Screen
        name="Profile"
        component={ProfileStack} // <-- USE THE PROFILE-STACK
        options={{
          tabBarIcon: ({ color, size }) => (
            <TabIcon name="person" color={color} size={size} />
          ),
        }}
      />
    </Tab.Navigator>
  );
}
